insert into article(id, article_type, created_at, updated_at, published_at, title, content)
	values('a-1a1920-fcbb-cama', 'GAME', '2019-10-20 00:00', '2019-10-20 00:00', '2019-10-20 00:00', 'FC Blešno B : Camelot 14:7 (4:5)',
	'<p>Na dnešní utkání béčko posílili Jára s Kubou, a protože Dan nedorazil, tak béčko odehrálo zápas s třemi hráči na střídání. Hosté dorazili s jedním hráčem na střídání.</p>
