insert into article(id, article_type, created_at, updated_at, published_at, title, content)
	values('a-kp1819-fcba-nkga', 'GAME', '2019-05-19 00:00', '2019-05-19 00:00', '2019-05-19 00:00', 'FC Blešno A : NK Growl 3:1 (0:0)',
