insert into article(id, article_type, created_at, updated_at, published_at, title, content)
	values('a-1b1819-gala-fcbb', 'GAME', '2019-04-07 00:00', '2019-04-07 00:00', '2019-04-07 00:00', 'Galaxy Juniors : FC Blešno B 7:2 (0:0)',
